package com.example.kotlin

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.navigation.fragment.findNavController
import com.example.kotlin.databinding.FragmentRVBinding


class RVFragment : Fragment() {
    private lateinit var bindind: FragmentRVBinding
    private lateinit var lottiAdapter: LottiAdapter
    private val lotttiList = ArrayList<Lottti>()
    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
       bindind = FragmentRVBinding.inflate(layoutInflater)
        return bindind.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        onLoad()
        initRV()

    }

    private fun initRV() {
        lottiAdapter = LottiAdapter(lotttiList){
            findNavController().navigate(RVFragmentDirections.actionLottiFragmentToDetailFragment(image = lotttiList[it].image))
//            requireActivity().supportFragmentManager.beginTransaction().replace(R.id.fragment_container,detailFragment).addToBackStack(null).commit()
        }
        bindind.rvLotti.adapter = lottiAdapter
    }

    private fun onLoad(){
        lotttiList.add(Lottti(image1))
        lotttiList.add(Lottti(image3))
        lotttiList.add(Lottti(image2))
    }
}
const val image1 = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTzO8OIJz-wvmnD1oQ2Fe_w-q9qR5dH2UYhsg&s"
const val image2 = "https://i.ytimg.com/vi/W7J3rsAzkwg/maxresdefault.jpg"
const val image3 = "data:image/jpeg;base64," +
        ""